DROP DATABASE IF EXISTS freddysFiles;
create database freddysFiles;
use freddysFiles;

DROP TABLE IF EXISTS Products;
CREATE TABLE Products (
	ProductName varchar(255),
    ProductImageUrl varchar(255),
    ProductDescription text(255),
    Price decimal(10, 2),
    SystemRequirements varchar(1000));
    

DROP TABLE IF EXISTS Users;
CREATE TABLE Users (
	UserName varchar(255),
    UserPassword varchar(255));
    

select * from Users;
select * from Products;